mapper.select:
select
  id, code, name, helpCode, sex, remark, attention
from t_checkgroup


        and id = #{checkGroup.id}


        and code = #{checkGroup.code}


        and name = #{checkGroup.name}


        and helpCode = #{checkGroup.helpcode}


        and sex = #{checkGroup.sex}


        and remark = #{checkGroup.remark}


        and attention = #{checkGroup.attention}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from t_checkgroup


        and id = #{id}


        and code = #{code}


        and name = #{name}


        and helpCode = #{helpcode}


        and sex = #{sex}


        and remark = #{remark}


        and attention = #{attention}
